import FilesUpload from '../packages/filesUpload/index';
import uploadHttp from '../packages/uploadHttp/index';

const components = [
    FilesUpload,
    uploadHttp
]


const install = function(Vue) {
    if (install.installed) return;
    components.map(component => {
        Vue.component(component.name, component);
    });
};


if (typeof window !== 'undefined' && window.Vue) {
    install(window.Vue);
}

export default {
    install,
    FilesUpload,
    uploadHttp
}