<template>
  <div class="main">
    <div class="left">
      <div class="top">
        <div style="display: flex">
          <a-progress
            :percent="50"
            size="small"
            status="active"
            :strokeWidth="30"
            :showInfo="false"
          />
          <div class="totalText">总空间20G</div>
        </div>
        <div class="text">
          <div>已使用123</div>
          <div>已使用123</div>
        </div>
      </div>
      <div class="echarts">
        <div id="echarts"></div>
      </div>
    </div>
    <div class="right">
      <div class="fileLog">
        <div class="title">文档日志({{ logList.length }}条记录)</div>
        <div
          class="content"
          :style="{
            'overflow-y': 'scroll',
            'overflow-x': 'hidden',
          }"
        >
          <div class="log" v-for="(item, index) in logList" :key="index">
            <div class="sign">
              <a-icon type="to-top" />
            </div>
            <div class="cot">
              <div class="title">
                <div class="text">
                  在{{ item.where_is }}{{ item.action_name }}了{{
                    item.file_number
                  }}
                </div>
                <div class="time">{{ showTime(item.create_time) }}</div>
              </div>
              <div class="info" v-for="(i, iKey) in item.file_name" :key="iKey">
                {{ i }}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import * as echarts from "echarts";
import axios from "axios";
import Vue from "vue";
Vue.prototype.$axios = axios;
export default {
  data() {
    return {
      token: "f0f3150d-37fe-4802-8958-6134f4fa3887",
      logList: [],
    };
  },
  created() {
    this.getLog();
  },
  mounted() {
    this.init();
    console.log(this.logList, 3322);
  },
  methods: {
    init() {
      this.showEcharts();
      //localhost:8004/cloudoffice/selectDocInfo?userName=小刘9   普通用户根据用户名查询
    },
    async getLog() {
      // let data = {
      //   key: "fileSystemSettings",
      // };
      await axios
        .get("http://192.168.19.29:6001/cloudoffice/selectDocInfo", {
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + this.token,
          },
          // params: {
          //   userName: "刘liu",
          // },
        })
        .then((res) => {
          if (res.status == 200) {
            console.log(res);
            //处理文件数量返回值由字符串转换成数组
            res.data.forEach((item) => {
              item.file_name = item.file_name.split(",");
            });
            this.logList = res.data;
          }
        })
        .catch((error) => {
          console.log(error);
        });
    },
    showEcharts() {
      let myChart = echarts.init(document.getElementById("echarts"));

      // 绘制图表
      let option = {
        tooltip: {
          trigger: "item",
          formatter: "{a} <br/>{b} : {c} ({d}%)",
        },
        legend: {
          bottom: "5%",
          left: "center",
        },

        series: [
          {
            name: "容量占比",
            type: "pie",
            radius: ["40%", "70%"],
            data: [
              { value: 1048, name: "Search Engine" },
              { value: 1048, name: "Sech Engine" },
              { value: 1048, name: "Search gine" },
              { value: 1048, name: "Seargine" },
            ],
          },
        ],
      };
      option && myChart.setOption(option);
    },
    showTime(val) {
      //返回的时间转换成时间戳
      let oldVal = new Date(val).getTime() / 1000;
      // 现在的时间转换成时间戳
      let now = new Date().getTime() / 1000;
      // 查看过了多长时间
      let result = now - oldVal;
      //如果时间大于一天时间，那么就取日期
      if (result > 86400000) {
        return val;
      } else {
        let ess = this.toHHmmss(result);
        console.log(ess);
        return ess;
      }
    },
    toHHmmss(data) {
      let hours = parseInt((data % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      let minutes = parseInt((data % (1000 * 60 * 60)) / (1000 * 60));
      // var seconds = (data % (1000 * 60)) / 1000;
      let time =
        (hours == "" ? "" : hours + "小时") +
        (minutes == "" ? "" : minutes) +
        "分前";
      // (seconds < 10 ? "0" + seconds : seconds);
      return time;
    },
  },
};
</script>

